#import "UIColor+Addition.h"

@implementation  UIColor (Addition)

+ (UIColor *)MMTextColor {
    return RGB(100, 100, 100);
}

+ (UIColor *)MMTextTipColor {
    return RGB(150, 150, 150);
}

+ (UIColor *)MMBlueColor {
    return RGB(20, 140, 226);
}

+ (UIColor *)MMGrayBackgroundColor {
    return RGB(225, 225, 225);
}

@end
