#import "UIFont+Addition.h"

@implementation UIFont (Addition)

+ (UIFont *)MMTextFont16 {
    return [UIFont systemFontOfSize:16];
}

+ (UIFont *)MMTextFont14 {
    return [UIFont systemFontOfSize:14];
}

+ (UIFont *)MMTextFont12 {
    return [UIFont systemFontOfSize:12];
}

@end
